import { Component } from '@angular/core';
import { FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'SeatManagement';
  ifsubmit:boolean=false;
  noofseats:any=0;
  seats=new FormControl('',Validators.required);
  submit(){
    this.ifsubmit=true;
    this.noofseats=this.seats.value
    // console.log(this.noofseats);

  }
}
<h1 [ngStyle]="{'text-align': 'center'}">Book Your Seats</h1>
<label for="seats"><b>Enter Number of Seats you want</b>&nbsp;&nbsp;&nbsp;
  <input type="text" [formControl]="seats">
</label>&nbsp;&nbsp;
<button (click)="submit()">SUBMIT</button>
<div *ngIf="ifsubmit==true">
  <app-seat [noofseats]="noofseats"></app-seat>
</div>
