import { Component } from '@angular/core';
import { FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'SeatManagement';
  ifsubmit:boolean=false;
  noofseats:number=0;
  seats=new FormControl(0,Validators.required);
  submit(){
    this.ifsubmit=true;
    this.noofseats=this.seats.getRawValue.length
    console.log(this.seats.getRawValue)

  }
}
<label for="seats"><b>Enter Number of Seats you want</b>&nbsp;&nbsp;&nbsp;
  <input type="text" [formControl]="seats">
</label>&nbsp;&nbsp;
<button (click)="submit()">SUBMIT</button>
<div *ngIf="ifsubmit==true">
  <app-seat></app-seat>
</div>
